package org.manager;import java.util.HashMap;import java.util.List;import java.util.Map;import org.model.User;import org.springframework.stereotype.Repository;import org.sys.base.BaseManagerImpl;@Repositorypublic class UserManager extends BaseManagerImpl<User, Integer> {	public Boolean addUser(User user) {		return this.save(user);	}		public Boolean updateUser(User user) {		return this.saveOrUpdate(user);	}		public Boolean deleteUser(User user) {		return this.delete(user);	}		public Boolean deleteUser(Integer id) {		return this.delete(id);	}		public Boolean deleteUserList(List<Integer> idList) {		for (Integer id : idList) {			deleteUser(id);		}		return true;	}		public User getUser(Integer id) {		return this.findById(id);	}		public List<User> getUserList() {		return this.find("from User order by role desc");	}		public User getUser(String username, String password) {		String[] propertyNames = {"username", "password"};		return this.findUniqueByPropertys(propertyNames, username, password);	}		public Map<Integer, User> getUserMap() {		List<User> userList = this.findAll();		Map<Integer, User> map = new HashMap<Integer, User>();		for (User user : userList) {			map.put(user.getId(), user);		}				return map;	}}